import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { Modal, Platform, Pressable, SectionList, StyleSheet, Text, View } from 'react-native';

const x = Platform.select({
  ios:'ios',
  android:'android',
  web:'web',
})
console.log(x)
export default function App() {
  const [modalVisible, setModalVisible] = React.useState(false);
  return (
    <View style={styles.container}>

      <Modal
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        onRequestClose={() => {
          Alert.alert("Modal has been closed.");
          setModalVisible(!modalVisible);
        }}
      >
      <View style={styles.centeredView}>
          <View style={styles.modalView}>
            <Text style={styles.modalText}>Hello World!</Text>
            <Pressable
              style={[styles.button, styles.buttonClose]}
              onPress={() => setModalVisible(!modalVisible)}
            >
              <Text style={styles.textStyle}>Hide Modal</Text>
            </Pressable>
          </View>
        </View>
      </Modal>
      <Pressable
        style={[styles.button, styles.buttonOpen]}
        onPress={() => setModalVisible(true)}
      >
        <Text style={styles.textStyle}>Show Modal</Text>
      </Pressable>
      <SectionList
        sections= {[
          {title:'Mobile', data:['iPhone x','iPhone 12']},
          {title:'Laptops', data:['hp','mac']},
        ]}
        ListHeaderComponent={<View><Text>big header</Text></View>}
        renderItem={({item})=><View><Text>{item}</Text></View>}
        renderSectionHeader={({section:{title}})=><View><Text style={{color:'red'}}>{title}</Text></View>}
        />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: 22
  },
  // modal:{
  //   flex:1,
  //   justifyContent:'center',
  //   alignContent:'center',
  //   alignItems:'center',
  // },
  modalView: {
    margin: 20,
    backgroundColor: "white",
    borderRadius: 20,
    padding: 35,
    alignItems: "center",
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2
    },
    shadowOpacity: 0.25,
    shadowRadius: 4,
    elevation: 5
  },
  button: {
    borderRadius: 20,
    padding: 10,
    elevation: 2
  },
  buttonOpen: {
    backgroundColor: "#F194FF",
  },
  buttonClose: {
    backgroundColor: "#2196F3",
  },
  textStyle: {
    color: "white",
    fontWeight: "bold",
    textAlign: "center"
  },
  modalText: {
    marginBottom: 15,
    textAlign: "center"
  }
});
